mapper.select:
select count(*) from hjy_cloud.T_APV_APPROVAL
where 1 = 1

    and PK_APPROVAL_ID = #{pkApprovalId}


    and APPROVAL_TYPE = #{approvalType}


    and APPROVAL_PEOPLE = #{approvalPeople}


    and NEXT_APPROVAL = #{nextApproval}


    and DATA_TYPE = #{dataType}


    and APV_STATION = #{apvStation}

select * from hjy_cloud.T_APV_APPROVAL


        and PK_APPROVAL_ID = #{pkApprovalId}


        and APPROVAL_TYPE = #{approvalType}


        and APPROVAL_PEOPLE = #{approvalPeople}


        and NEXT_APPROVAL = #{nextApproval}


        and DATA_TYPE = #{dataType}


        and APV_STATION = #{apvStation}


        and IS_ENDING = #{isEnding}


        and IS_START = #{isStart}

select
PK_APPROVAL_ID,
APPROVAL_TYPE,
NEXT_APPROVAL,
DATA_TYPE,
APV_STATION,
IS_ENDING,
IS_START,
T_STAFF_INFO.STAFF_NAME as APPROVAL_PEOPLE
from T_APV_APPROVAL
LEFT JOIN
T_STAFF_INFO
on
T_STAFF_INFO.PK_STAFF_ID = T_APV_APPROVAL.APPROVAL_PEOPLE


        and PK_APPROVAL_ID = #{pkApprovalId}


        and APPROVAL_TYPE = #{approvalType}


        and APPROVAL_PEOPLE = #{approvalPeople}


        and NEXT_APPROVAL = #{nextApproval}


        and DATA_TYPE = #{dataType}


        and APV_STATION = #{apvStation}


        and IS_ENDING = #{isEnding}


        and IS_START = #{isStart}

SELECT T_APV_APPROVAL.PK_APPROVAL_ID, T_APV_APVTYPE.APVTYPE_NAME as APPROVAL_TYPE, T_APV_APPROVAL.APPROVAL_PEOPLE, T_APV_APPROVAL.NEXT_APPROVAL, T_APV_APPROVAL.DATA_TYPE, T_APV_APPROVAL.APV_STATION, T_APV_APPROVAL.IS_ENDING, T_APV_APPROVAL.IS_START FROM T_APV_APPROVAL INNER JOIN T_APV_APVTYPE ON T_APV_APPROVAL.APPROVAL_TYPE = T_APV_APVTYPE.PK_APVTYPE_ID
and PK_APPROVAL_ID = #{pkApprovalId}
and APPROVAL_TYPE = #{approvalType}
and IS_START = #{isStart}
and DATA_TYPE = #{dataType}
