mapper.select:
select count(*) from T_STAFF_INFO


        and PK_STAFF_ID = #{pkStaffId,jdbcType=VARCHAR}


        and STAFF_NAME = #{staffName,jdbcType=VARCHAR}


        and STAFF_SEX = #{staffSex,jdbcType=INTEGER}


        and STAFF_AGE = #{staffAge,jdbcType=INTEGER}


        and STAFF_STATUS = #{staffStatus,jdbcType=INTEGER}


        and FK_DEPT_ID = #{fkDeptId,jdbcType=VARCHAR}


        and FK_POSITION_ID = #{fkPositionId,jdbcType=VARCHAR}


        and FK_WORKADDRESS_ID = #{fkWorkaddressId,jdbcType=VARCHAR}


        and FK_HTLX_ID = #{fkHtlxId,jdbcType=VARCHAR}


        and RECRUIT_WAY = #{recruitWay,jdbcType=VARCHAR}


        and ID_TYPE = #{idType,jdbcType=VARCHAR}


        and ID_CARD = #{idCard,jdbcType=VARCHAR}


        and STAFF_EMAIL = #{staffEmail,jdbcType=VARCHAR}


        and STAFF_TEL = #{staffTel,jdbcType=VARCHAR}


        and NATIVE_PLACE = #{nativePlace,jdbcType=VARCHAR}


        and BIRTHDAY = #{birthday,jdbcType=VARCHAR}


        and FK_NATION_ID = #{fkNationId,jdbcType=VARCHAR}


        and HKSZD = #{hkszd,jdbcType=VARCHAR}


        and POLITIVAL_OUTLOOK = #{politivalOutlook,jdbcType=VARCHAR}


        and CURRENT_ADDRESS = #{currentAddress,jdbcType=VARCHAR}


        and IS_MARRY = #{isMarry,jdbcType=INTEGER}


        and FK_EDUCATION_ID = #{fkEducationId,jdbcType=VARCHAR}


        and ZGXLBYYX = #{zgxlbyyx,jdbcType=VARCHAR}


        and WY_DJ = #{wyDj,jdbcType=VARCHAR}


        and JSJ_DJ = #{jsjDj,jdbcType=VARCHAR}


        and MAJOR = #{major,jdbcType=VARCHAR}


        and BANK_NAME = #{bankName,jdbcType=VARCHAR}


        and BANK_ID = #{bankId,jdbcType=VARCHAR}


        and BRANCH_BANK = #{branchBank,jdbcType=VARCHAR}


        and SYR = #{syr,jdbcType=VARCHAR}


        and PICTURE_PATH = #{picturePath,jdbcType=VARCHAR}


        and HBLX_NAME1 = #{hblxName1,jdbcType=VARCHAR}


        and OBJECT1 = #{object1,jdbcType=VARCHAR}


        and HBLX_NAME2 = #{hblxName2,jdbcType=VARCHAR}


        and OBJECT2 = #{object2,jdbcType=VARCHAR}


        and HBLX_NAME3 = #{hblxName3,jdbcType=VARCHAR}


        and OBJECT3 = #{object3,jdbcType=VARCHAR}


        and HBLX_NAME4 = #{hblxName4,jdbcType=VARCHAR}


        and OBJECT4 = #{object4,jdbcType=VARCHAR}


        and HBLX_NAME5 = #{hblxName5,jdbcType=VARCHAR}


        and OBJECT5 = #{object5,jdbcType=VARCHAR}

SELECT PK_STAFF_ID, STAFF_NAME, STAFF_SEX, STAFF_AGE, STAFF_STATUS, T_STAFF_INFO.FK_DEPT_ID, T_OUTFIT_DEPT.DEPT_NAME AS deptName, T_STAFF_INFO.FK_POSITION_ID, T_DICTIONARY_POSITION.POSITION_NAME AS positionName, T_STAFF_INFO.FK_WORKADDRESS_ID, T_OUTFIT_WORKADDRESS.WORKADRESS_NAME AS workAddressName, ENTRY_TIME, T_STAFF_INFO.FK_HTLX_ID, T_DICTIONARY_HTLX.CONTRAC_TYPE AS htlxName, RECRUIT_WAY, SYQDQ_TIME, ID_TYPE, ID_CARD, STAFF_EMAIL, STAFF_TEL, NATIVE_PLACE, BIRTHDAY, T_STAFF_INFO.FK_NATION_ID, T_DICTIONARY_NATION.NATION_NAME AS nationName, HKSZD, POLITIVAL_OUTLOOK, CURRENT_ADDRESS, IS_MARRY, T_STAFF_INFO.FK_EDUCATION_ID, T_DICTIONARY_EDUCATION.EDUCATION_NAME AS educationName, T_STAFF_INFO.ZGXLBYYX, T_STAFF_INFO.WY_DJ, T_STAFF_INFO.JSJ_DJ, T_STAFF_INFO.MAJOR, T_STAFF_INFO.BANK_NAME, T_STAFF_INFO.BANK_ID, T_STAFF_INFO.BRANCH_BANK, T_STAFF_INFO.SYR, T_STAFF_INFO.PICTURE_PATH, T_STAFF_INFO.HBLX_NAME1, T_STAFF_INFO.OBJECT1, T_STAFF_INFO.HBLX_NAME2, T_STAFF_INFO.OBJECT2, T_STAFF_INFO.HBLX_NAME3, T_STAFF_INFO.OBJECT3, T_STAFF_INFO.HBLX_NAME4, T_STAFF_INFO.OBJECT4, T_STAFF_INFO.HBLX_NAME5, T_STAFF_INFO.OBJECT5 FROM T_STAFF_INFO LEFT JOIN T_OUTFIT_DEPT ON T_STAFF_INFO.FK_DEPT_ID = T_OUTFIT_DEPT.PK_DEPT_ID LEFT JOIN T_DICTIONARY_POSITION ON T_STAFF_INFO.FK_POSITION_ID = T_DICTIONARY_POSITION.PK_POSITION_ID LEFT JOIN T_OUTFIT_WORKADDRESS ON T_OUTFIT_WORKADDRESS.PK_WORKADRESS_ID = T_STAFF_INFO.FK_WORKADDRESS_ID LEFT JOIN T_DICTIONARY_HTLX ON T_DICTIONARY_HTLX.PK_HTLX_ID = T_STAFF_INFO.FK_HTLX_ID LEFT JOIN T_DICTIONARY_NATION ON T_STAFF_INFO.FK_NATION_ID = T_DICTIONARY_NATION.PK_NATION_ID LEFT JOIN T_DICTIONARY_EDUCATION ON T_STAFF_INFO.FK_EDUCATION_ID = T_DICTIONARY_EDUCATION.PK_EDUCATION_ID where 1 = 1
and PK_STAFF_ID = #{pkStaffId}
and STAFF_NAME = #{staffName}
and STAFF_SEX = #{staffSex}
and FK_DEPT_ID = #{fkDeptId}
and FK_POSITION_ID = #{fkPositionId}
and ID_CARD = #{idCard}

SELECT PK_STAFF_ID, STAFF_NAME, STAFF_SEX, STAFF_AGE, STAFF_STATUS, T_OUTFIT_DEPT.DEPT_NAME AS FK_DEPT_ID, T_DICTIONARY_POSITION.POSITION_NAME AS FK_POSITION_ID, ENTRY_TIME, T_DICTIONARY_HTLX.CONTRAC_TYPE AS FK_HTLX_ID, RECRUIT_WAY, SYQDQ_TIME, ID_TYPE, ID_CARD, STAFF_EMAIL, STAFF_TEL, NATIVE_PLACE, BIRTHDAY, T_DICTIONARY_NATION.NATION_NAME AS FK_NATION_ID, HKSZD, POLITIVAL_OUTLOOK, CURRENT_ADDRESS, IS_MARRY, T_DICTIONARY_EDUCATION.EDUCATION_NAME AS FK_EDUCATION_ID, T_STAFF_INFO.ZGXLBYYX, T_STAFF_INFO.WY_DJ, T_STAFF_INFO.JSJ_DJ, T_STAFF_INFO.MAJOR, T_STAFF_INFO.BANK_NAME, T_STAFF_INFO.BANK_ID, T_STAFF_INFO.BRANCH_BANK, T_STAFF_INFO.SYR, T_STAFF_INFO.PICTURE_PATH, T_STAFF_INFO.HBLX_NAME1, T_STAFF_INFO.OBJECT1, T_STAFF_INFO.HBLX_NAME2, T_STAFF_INFO.OBJECT2, T_STAFF_INFO.HBLX_NAME3, T_STAFF_INFO.OBJECT3, T_STAFF_INFO.HBLX_NAME4, T_STAFF_INFO.OBJECT4, T_STAFF_INFO.HBLX_NAME5, T_STAFF_INFO.OBJECT5 FROM T_STAFF_INFO LEFT JOIN T_OUTFIT_DEPT ON T_STAFF_INFO.FK_DEPT_ID = T_OUTFIT_DEPT.PK_DEPT_ID LEFT JOIN T_DICTIONARY_POSITION ON T_STAFF_INFO.FK_POSITION_ID = T_DICTIONARY_POSITION.PK_POSITION_ID LEFT JOIN T_DICTIONARY_HTLX ON T_DICTIONARY_HTLX.PK_HTLX_ID = T_STAFF_INFO.FK_HTLX_ID LEFT JOIN T_DICTIONARY_NATION ON T_STAFF_INFO.FK_NATION_ID = T_DICTIONARY_NATION.PK_NATION_ID LEFT JOIN T_DICTIONARY_EDUCATION ON T_STAFF_INFO.FK_EDUCATION_ID = T_DICTIONARY_EDUCATION.PK_EDUCATION_ID
and STAFF_NAME = #{staffName}
and STAFF_SEX = #{staffSex}
and FK_DEPT_ID = #{fkDeptId}
and FK_POSITION_ID = #{fkPositionId}
and ID_CARD = #{idCard}
order by ENTRY_TIME desc

